import tkinter as tkfrom updater import check_and_updateclass SimpleApp(tk.Tk):    def __init__(self):        super().__init__()        self.title("Auto-Update Demo")        self.geometry("300x200")        tk.Label(self, text="My App", font=("Arial", 18)).pack(pady=20)        tk.Button(self, text="Check for Updates", command=check_and_update).pack()        # Auto check after launch        self.after(2000, check_and_update)if __name__ == "__main__":    SimpleApp().mainloop()