import requestsimport subprocessimport sysimport osfrom app import __version__GITHUB_OWNER = "megaee"GITHUB_REPO = "Version-Control"def check_and_update():    """    Compare local version with latest Git tag.    Download latest .exe release if newer version exists.    """    api_url = f"https://api.github.com/repos/{GITHUB_OWNER}/{GITHUB_REPO}/releases/latest"    response = requests.get(api_url).json()    latest_version = response["tag_name"].lstrip('v')    if latest_version == __version__:        return  # Already up to date    asset = None    for a in response["assets"]:        if a["name"].endswith(".exe"):            asset = a            break    if not asset:        print("No .exe release asset found.")        return    download_url = asset["browser_download_url"]    file_name = asset["name"]    print(f"Downloading update: {file_name} ...")    r = requests.get(download_url)    with open(file_name, "wb") as f:        f.write(r.content)    print("Running installer...")    subprocess.Popen([file_name])    sys.exit()  # close app after starting installer